import React, { useContext } from 'react';
import { JobContext } from '../main/JobContext';

const SearchButton = ( { filterName, locationName, fulltime } ) => {

    const [jobs, setJobs] = useContext(JobContext);

    const filterCards = () => {

        return setJobs(jobs.map(job => {
           if(job.companyName.toLowerCase().contains(filterName.toLowerCase())){
                return {...job, job};
           }else {
               return job;
           }
        }))

        // return setJobs( jobs.map(job => job.companyName === "Scoot" ? {...job, companyName:"Pepsi"} :  job.companyName === "Pepsi" ? {...job, companyName: "Scoot"} : job) )
    }

    return (
        <div className="search-button search-component h-100 d-flex">
            <button onClick={filterCards}>
                Search
            </button>
        </div>
    )
}

export default SearchButton;
