    const [jobs, setJobs] = useContext(JobContext);
    const [baseJobs, setBaseJobs] = useState(data);

    const filterCards = () => {

        setJobs(jobs.filter(job => {
            if(job.companyName.toLowerCase().includes(filterName.toLowerCase())){
                return job;
           }else {
               return data
           }
        }))

        // return setJobs( jobs.map(job => job.companyName === "Scoot" ? {...job, companyName:"Pepsi"} :  job.companyName === "Pepsi" ? {...job, companyName: "Scoot"} : job) )
